(518, 'um_options', 'a:176:{s:8:"last_tab";s:0:"";s:9:"core_user";s:2:"17";s:12:"core_account";s:2:"27";s:12:"core_members";s:2:"23";s:13:"core_register";s:2:"21";s:10:"core_login";s:2:"19";s:11:"core_logout";s:2:"25";s:19:"core_password-reset";s:2:"29";s:12:"default_role";s:6:"member";s:14:"permalink_base";s:10:"user_login";s:12:"display_name";s:9:"full_name";s:18:"display_name_field";s:0:"";s:15:"author_redirect";s:1:"1";s:12:"members_page";s:1:"1";s:13:"use_gravatars";s:1:"0";s:24:"reset_require_strongpass";s:1:"0";s:20:"account_tab_password";s:1:"1";s:19:"account_tab_privacy";s:1:"1";s:25:"account_tab_notifications";s:1:"1";s:18:"account_tab_delete";s:1:"1";s:19:"delete_account_text";s:150:"Are you sure you want to delete your account? This will erase all of your account data from the site. To delete your account enter your password below";s:12:"account_name";s:1:"1";s:13:"account_email";s:1:"1";s:26:"account_require_strongpass";s:1:"0";s:9:"panic_key";s:10:"38jyxF2JVV";s:10:"accessible";s:1:"0";s:15:"access_redirect";s:0:"";s:13:"wpadmin_login";s:1:"1";s:25:"deny_admin_frontend_login";s:1:"1";s:22:"wpadmin_login_redirect";s:13:"um_login_page";s:26:"wpadmin_login_redirect_url";s:0:"";s:16:"wpadmin_register";s:1:"1";s:25:"wpadmin_register_redirect";s:16:"um_register_page";s:29:"wpadmin_register_redirect_url";s:0:"";s:24:"access_widget_admin_only";s:1:"1";s:17:"wpadmin_allow_ips";s:0:"";s:11:"blocked_ips";s:0:"";s:14:"blocked_emails";s:0:"";s:13:"blocked_words";s:47:"admin\r\nadministrator\r\nwebmaster\r\nsupport\r\nstaff";s:9:"mail_from";s:4:"TMDT";s:14:"mail_from_addr";s:23:"vanlong200880@gmail.com";s:10:"email_html";s:1:"1";s:16:"welcome_email_on";s:1:"1";s:17:"welcome_email_sub";s:23:"Welcome to {site_name}!";s:13:"welcome_email";s:341:"Hi {display_name},\r\n\r\nThank you for signing up with {site_name}! Your account is now active.\r\n\r\nTo login please visit the following url:\r\n\r\n{login_url}\r\n\r\nYour account e-mail: {email}\r\nYour account username: {username}\r\nYour account password: {password}\r\n\r\nIf you have any problems, please contact us at {admin_email}\r\n\r\nThanks,\r\n{site_name}";s:18:"checkmail_email_on";s:1:"1";s:19:"checkmail_email_sub";s:28:"Please activate your account";s:15:"checkmail_email";s:266:"Hi {display_name},\r\n\r\nThank you for signing up with {site_name}! To activate your account, please click the link below to confirm your email address:\r\n\r\n{account_activation_link}\r\n\r\nIf you have any problems, please contact us at {admin_email}\r\n\r\nThanks,\r\n{site_name}";s:16:"pending_email_on";s:1:"1";s:17:"pending_email_sub";s:30:"Your account is pending review";s:13:"pending_email";s:272:"Hi {display_name},\r\n\r\nThank you for signing up with {site_name}! Your account is currently being reviewed by a member of our team.\r\n\r\nPlease allow us some time to process your request.\r\n\r\nIf you have any problems, please contact us at {admin_email}\r\n\r\nThanks,\r\n{site_name}";s:17:"approved_email_on";s:1:"1";s:18:"approved_email_sub";s:41:"Your account at {site_name} is now active";s:14:"approved_email";s:363:"Hi {display_name},\r\n\r\nThank you for signing up with {site_name}! Your account has been approved and is now active.\r\n\r\nTo login please visit the following url:\r\n\r\n{login_url}\r\n\r\nYour account e-mail: {email}\r\nYour account username: {username}\r\nYour account password: {password}\r\n\r\nIf you have any problems, please contact us at {admin_email}\r\n\r\nThanks,\r\n{site_name}";s:17:"rejected_email_on";s:1:"1";s:18:"rejected_email_sub";s:30:"Your account has been rejected";s:14:"rejected_email";s:260:"Hi {display_name},\r\n\r\nThank you for applying for membership to {site_name}! We have reviewed your information and unfortunately we are unable to accept you as a member at this moment.\r\n\r\nPlease feel free to apply again at a future date.\r\n\r\nThanks,\r\n{site_name}";s:17:"inactive_email_on";s:1:"1";s:18:"inactive_email_sub";s:33:"Your account has been deactivated";s:14:"inactive_email";s:222:"Hi {display_name},\r\n\r\nThis is an automated email to let you know your {site_name} account has been deactivated.\r\n\r\nIf you would like your account to be reactivated please contact us at {admin_email}\r\n\r\nThanks,\r\n{site_name}";s:17:"deletion_email_on";s:1:"1";s:18:"deletion_email_sub";s:29:"Your account has been deleted";s:14:"deletion_email";s:332:"Hi {display_name},\r\n\r\nThis is an automated email to let you know your {site_name} account has been deleted. All of your personal information has been permanently deleted and you will no longer be able to login to {site_name}.\r\n\r\nIf your account has been deleted by accident please contact us at {admin_email}\r\n\r\nThanks,\r\n{site_name}";s:16:"resetpw_email_on";s:1:"1";s:17:"resetpw_email_sub";s:19:"Reset your password";s:13:"resetpw_email";s:266:"Hi {display_name},\r\n\r\nWe received a request to reset the password for your account. If you made this request, click the link below to change your password:\r\n\r\n{password_reset_link}\r\n\r\nIf you didn''t make this request, you can ignore this email\r\n\r\nThanks,\r\n{site_name}";s:18:"changedpw_email_on";s:1:"1";s:19:"changedpw_email_sub";s:42:"Your {site_name} password has been changed";s:15:"changedpw_email";s:279:"Hi {display_name},\r\n\r\nYou recently changed the password associated with your {site_name} account.\r\n\r\nIf you did not make this change and believe your {site_name} account has been compromised, please contact us at the following email address: {admin_email}\r\n\r\nThanks,\r\n{site_name}";s:11:"admin_email";s:23:"vanlong200880@gmail.com";s:24:"notification_new_user_on";s:1:"1";s:25:"notification_new_user_sub";s:30:"[{site_name}] New user account";s:21:"notification_new_user";s:187:"{display_name} has just created an account on {site_name}. To view their profile click here:\r\n\r\n{user_profile_link}\r\n\r\nHere is the submitted registration form:\r\n\r\n{submitted_registration}";s:22:"notification_review_on";s:1:"0";s:23:"notification_review_sub";s:38:"[{site_name}] New user awaiting review";s:19:"notification_review";s:245:"{display_name} has just applied for membership to {site_name} and is waiting to be reviewed.\r\n\r\nTo review this member please click the following link:\r\n\r\n{user_profile_link}\r\n\r\nHere is the submitted registration form:\r\n\r\n{submitted_registration}";s:24:"notification_deletion_on";s:1:"0";s:25:"notification_deletion_sub";s:29:"[{site_name}] Account deleted";s:21:"notification_deletion";s:58:"{display_name} has just deleted their {site_name} account.";s:22:"profile_photo_max_size";s:0:"";s:20:"cover_photo_max_size";s:0:"";s:17:"photo_thumb_sizes";a:3:{i:0;s:2:"40";i:1;s:2:"80";i:2;s:3:"190";}s:17:"cover_thumb_sizes";a:2:{i:0;s:3:"300";i:1;s:3:"600";}s:17:"image_compression";s:2:"60";s:15:"image_max_width";s:4:"1000";s:15:"cover_min_width";s:4:"1000";s:13:"profile_title";s:21:"{display_name} | TMDT";s:12:"profile_desc";s:83:"{display_name} is on {site_name}. Join {site_name} to view {display_name}''s profile";s:18:"directory_template";s:7:"members";s:12:"active_color";s:7:"#3ba1da";s:15:"secondary_color";s:7:"#44b0ec";s:17:"primary_btn_color";s:7:"#3ba1da";s:17:"primary_btn_hover";s:7:"#44b0ec";s:16:"primary_btn_text";s:7:"#ffffff";s:19:"secondary_btn_color";s:7:"#eeeeee";s:19:"secondary_btn_hover";s:7:"#e5e5e5";s:18:"secondary_btn_text";s:7:"#666666";s:14:"help_tip_color";s:7:"#cccccc";s:16:"form_field_label";s:7:"#555555";s:11:"form_border";s:14:"2px solid #ddd";s:17:"form_border_hover";s:14:"2px solid #bbb";s:13:"form_bg_color";s:7:"#ffffff";s:19:"form_bg_color_focus";s:7:"#ffffff";s:15:"form_text_color";s:7:"#666666";s:16:"form_placeholder";s:7:"#aaaaaa";s:15:"form_icon_color";s:7:"#aaaaaa";s:13:"form_asterisk";s:1:"0";s:19:"form_asterisk_color";s:7:"#aaaaaa";s:16:"profile_template";s:7:"profile";s:17:"profile_max_width";s:6:"1000px";s:22:"profile_area_max_width";s:5:"600px";s:13:"profile_align";s:6:"center";s:13:"profile_i